class QueryParser
{
    private $db;
    
    public function construct(Database $db)
    {
        $this->db=$db;	
    }
    
    public function parse($equation)
    {
    	$equation=trim($equation);
        if ($equation==='') return false;
        
        $t=token_get_all('<?php ' . $equation . '?>');
        foreach ($t as $index=>$token)
        {
            // Le token est un bloc de texte
            if (is_string($token))
            {
                echo $index, '. texte : [', htmlentities($token), ']<br />';
            }
            else
            {
                list($type, $data) = $token;
                
                echo $index,'. ', token_name($type), ' : [', htmlentities($data), ']<br />';
            }
        }
    }
}

