    public function actionAscoLoad()
    {
        set_time_limit(0);
//        echo '<pre>', var_export(Config::getAll(), true), '</pre>';
        // charge le .def de ascodocpsy
        echo 'Chargement du .def<br />';
        $def=file_get_contents(dirname(__FILE__).'/ascodocpsy.def.xml');
        
        // crée la base
        echo 'Création de la base xapian dans ', DB_PATH, '<br />';
        $xapianDb=Database::create(DB_PATH, $def, 'xapian');
        
        // Importe des notices de la base bis dans la base xapian
        echo 'Ouverture de la base BIS : ', BIS_PATH, '<br />';
        $bisDb=Database::open(BIS_PATH, true, 'bis');
                
        echo 'Lancement d\'une recherche * dans la base BIS<br />';
        if (!$bisDb->search('*', array('_sort'=>'-','_start'=>1,'_max'=>-1)))
            die('aucune réponse');


        echo '<hr />';
        echo $bisDb->count(), ' notices à charger à partir de la base BIS<br />';
        echo '<hr />';
        
        while(ob_get_level()) ob_end_flush();
        echo '<pre>';
        echo 'nb total de notices chargées; secondes depuis le début; secondes depuis précédent; nb de notices par seconde<br />';            
        $nb=0;
        $startTime=$time=microtime(true);
        foreach($bisDb as $record)
        {
            if ($nb %100 == 0)
            {
                $lastTime=$time;
                $time=microtime(true);
                echo sprintf
                (
                    '%6d ; %8.2f ; %6.2f ; %6.2f<br />', 
                    $nb,
                    $time-$startTime,
                    $time-$lastTime,
                    $nb==0 ? 0.0: 100/($time-$lastTime)
                );
                flush();
            }
            $xapianDb->addRecord();

            foreach($record as $name=>$value)
            {
                if (in_array($name, array('Aut','Edit','Lieu','MotCle','Nomp','CanDes','EtatCol', 'Loc','ProdFich')))
                {
                    $value=explode('/',$value);
                    if (count($value)===1) $value=$value[0];
                }
                $xapianDb[$name]=$value;
            }
            $xapianDb->saveRecord();
            $nb++;
        }

        // infos du dernier lot chargé
        $lastTime=$time;
        $time=microtime(true);
        echo sprintf
        (
            '%6d ; %8.2f ; %6.2f ; %6.2f<br />', 
            $nb,
            $time-$startTime,
            $time-$lastTime,
            $nb==0 ? 0.0: 100/($time-$lastTime)
        );
        flush();
        
        echo 'Fermeture (et flush) de la base<br />';
        unset($bisDb);
        unset($xapianDb);

        // pour mesurer le temps de fermeture
        $lastTime=$time;
        $time=microtime(true);
        echo sprintf
        (
            '%6d ; %8.2f ; %6.2f ; %6.2f<br />', 
            $nb,
            $time-$startTime,
            $time-$lastTime,
            0
        );
        flush();
        
        echo 'Terminé<br />';
    }
    
    public function actionBisToXapian()
    {
        $bisDb=Database::open('ascodocpsy', true);
                
        if (!$bisDb->search('Type=rapport', array('_sort'=>'+','_start'=>1,'_max'=>100)))
            die('aucune réponse');
            
        $xapianDb=Database::open(DB_PATH, false, 'xapian');

        foreach($bisDb as $record)
        {
            $xapianDb->addRecord();
            foreach($record as $name=>$value)
            {
                if ($value) echo $name, ' : ', $value, "<br />";
                $xapianDb[$name]=$value;
            }
            $xapianDb->saveRecord();
            echo '<hr />';
        }
        die('ok');
    }
    
